// http://ejohn.org/blog/ecmascript-5-strict-mode-json-and-more/
"use strict";

// Optional. You will see this name in eg. 'ps' or 'top' command
process.title = 'concurrent-form-update';

// Port where we'll run the websocket server
var webSocketsServerPort = 1337;

// websocket and http servers
var webSocketServer = require('websocket').server;
var http = require('http');

var concurrentUpdateDataHolder = {};

// Connected clients
var clients = [];
var filteredClients = {};

/**
 * Helper function for escaping input strings
 */
function htmlEntities(str) {
    return String(str).replace(/&/g, '&amp;').replace(/</g, '&lt;')
        .replace(/>/g, '&gt;').replace(/"/g, '&quot;');
}


/**
 * HTTP server
 */
var server = http.createServer(function (request, response) {
    // Not important for us. We're writing WebSocket server, not HTTP server
});
server.listen(webSocketsServerPort, function () {
    console.log((new Date()) + " Server is listening on port " + webSocketsServerPort);
});

/**
 * WebSocket server
 */
var wsServer = new webSocketServer({
    // WebSocket server is tied to a HTTP server. WebSocket request is just
    // an enhanced HTTP request. For more info http://tools.ietf.org/html/rfc6455#page-6
    httpServer: server
});

// This callback function is called every time someone
// tries to connect to the WebSocket server
wsServer.on('request', function (request) {
    console.log((new Date()) + ' Connection from origin ' + request.origin + '.');

    // accept connection - you should check 'request.origin' to make sure that
    // client is connecting from your website
    // (http://en.wikipedia.org/wiki/Same_origin_policy)
    var connection = request.accept(null, request.origin);

    clients.push(connection);

    if (!('concurrentUpdateData' in concurrentUpdateDataHolder)) {
        concurrentUpdateDataHolder["concurrentUpdateData"] = {};
        console.log("concurrentUpdateDataHolder init");
    }

    // user sent some message
    connection.on('message', function (message) {
        var reqJson = {};
        var objString = "";
        if (message.type === 'utf8') {
            reqJson = JSON.parse(message.utf8Data);
            if (!(reqJson.providerId in filteredClients)) {
                filteredClients[reqJson.providerId] = {
                    "connection": connection
                }
                
                // Preparing a string as a object and using magic of JSON.parse
                /*objString = '{"' + reqJson.patientId + '":{"' 
                    + reqJson.formId + '":{"' 
                    + reqJson.userId + '":{'
                    + '"fields":' + JSON.stringify(reqJson.fields)
                    + '}}}}';
                
                concurrentUpdateDataHolder.concurrentUpdateData[reqJson.providerId] = JSON.parse(objString);*/
            }
            
            if(reqJson.providerId in concurrentUpdateDataHolder.concurrentUpdateData) {
                if (reqJson.patientId in concurrentUpdateDataHolder.concurrentUpdateData[reqJson.providerId]) {
                    if(reqJson.formId in concurrentUpdateDataHolder.concurrentUpdateData[reqJson.providerId][reqJson.patientId]) {
                        if(reqJson.userId in concurrentUpdateDataHolder.concurrentUpdateData[reqJson.providerId][reqJson.patientId][reqJson.formId]) {
                            if("fields" in concurrentUpdateDataHolder.concurrentUpdateData[reqJson.providerId][reqJson.patientId][reqJson.formId][reqJson.userId]) {
                                concurrentUpdateDataHolder.concurrentUpdateData[reqJson.providerId][reqJson.patientId][reqJson.formId][reqJson.userId]["fields"] = reqJson.fields;
                            } else {
                                
                            }
                        } else {
                            
                        }
                    } else {
                        
                    }                    
                } else {
                    
                }
            } else {
                
            }
        }
    });

    // user disconnected
    connection.on('close', function (connection) {
        console.log(connection);
    });
});